    /**
     * @param text field and string to set placeholder from text fields
     */
    private void setTextFieldPlaceholder(javax.swing.JTextField textField, String text) {
        if (textField.getText().equalsIgnoreCase(text)) {
            textField.setText("");
            textField.setForeground(new Color(153, 153, 153));
        }
    }

    /**
     * @param text field and string to remove placeholder from text fields
     */
    private void removeTextFieldPlaceholder(javax.swing.JTextField textField, String text) {
        if (textField.getText().equals("")) {
            textField.setText(text);
            textField.setForeground(new Color(153, 153, 153));
        }
    }

    /**
     * @add data to the database
     */
    @SuppressWarnings("UnusedAssignment")
    private void addStudent() {
        String url = "jdbc:mysql://localhost:3306/library catalog system";
        String username = "Jareth";
        String password = "Jareth0223";
        Connection connection = null;
        PreparedStatement preparedStatement = null;
        try {
            connection = DriverManager.getConnection(url, username, password);
            String insertQuery = "INSERT INTO students (studentID, fullName, userName, email, password, phoneNumber, address, course, yearLevel, section, booksBorrowed, booksReturned) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";
            preparedStatement = connection.prepareStatement(insertQuery);
            preparedStatement.setString(1, studentIDTextField.getText());
            preparedStatement.setString(2, firstNameTextField.getText() + " " + middleNameTextField.getText() + " " + lastNameTextField.getText());
            preparedStatement.setString(3, usernameTextField.getText());
            preparedStatement.setString(4, emailTextField.getText());
            preparedStatement.setString(5, encryptPassword(String.valueOf(passwordTextField.getPassword())));
            preparedStatement.setString(6, phoneNumberTextField.getText());
            preparedStatement.setString(7, addressTextField.getText());
            preparedStatement.setString(8, courseComboBox.getSelectedItem().toString());
            preparedStatement.setInt(9, Integer.parseInt(yearLevelComboBox.getSelectedItem().toString()));
            preparedStatement.setString(10, sectionTextField.getText());
            preparedStatement.setInt(11, 0);
            preparedStatement.setInt(12, 0);
            int rowsAffected = preparedStatement.executeUpdate();
            if (rowsAffected > 0) {
                System.out.println("Data added successfully!");
                clearFields();
            } else {
                System.out.println("Failed to add data.");
            }
        } catch (NumberFormatException | SQLException e) {
            System.out.println("Error: " + e.getMessage());
        }
    }

